import axios from "axios";

const API = axios.create({
  baseURL: "http://127.0.0.1:8000", // backend ka sahi URL
});

// Fetch all incidents
export const fetchIncidents = async () => {
  const res = await API.get("/api/incidents");
  return res.data;
};

// Example extra APIs (future use)
export const fetchReports = async () => {
  const res = await API.get("/api/reports");
  return res.data;
};

export const fetchSimulatorData = async () => {
  const res = await API.get("/api/simulator");
  return res.data;
};
