import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { fetchIncidents } from "../services/api";

export default function Incidents() {
  const [incidents, setIncidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      setError("");
      try {
        const data = await fetchIncidents();
        setIncidents(data);
      } catch (err) {
        setError(err.message);
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [navigate]);

  if (loading) return <p className="text-gray-500 text-center mt-6">Loading incidents...</p>;
  if (error) return <p className="text-red-500 text-center mt-6">{error}</p>;

  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <h2 className="text-2xl font-bold mb-4 text-center">Incidents</h2>

      {incidents.length === 0 ? (
        <p className="text-gray-500 text-center mt-4">No incidents found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
            <thead className="bg-gray-800 text-white">
              <tr>
                <th className="px-4 py-2 text-left">Type</th>
                <th className="px-4 py-2 text-left">Severity</th>
                <th className="px-4 py-2 text-left">Timestamp</th>
                <th className="px-4 py-2 text-left">Details</th>
              </tr>
            </thead>
            <tbody>
              {incidents.map((inc) => (
                <tr key={inc.id} className="border-b hover:bg-gray-50 transition-colors">
                  <td className="px-4 py-2">{inc.type}</td>
                  <td className="px-4 py-2">{inc.severity}</td>
                  <td className="px-4 py-2">{inc.timestamp || "-"}</td>
                  <td className="px-4 py-2">
                    <pre className="text-sm">{JSON.stringify(inc.details, null, 2)}</pre>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
