import { useState } from "react";

export default function Simulator() {
  const [result, setResult] = useState(null);
  const [note, setNote] = useState("");
  const [message, setMessage] = useState("");

  const createFiles = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/simulate/create-files?count=10", {
        method: "POST",
      });
      const data = await res.json();
      setMessage(`‚úÖ ${data.created} dummy files created in sandbox.`);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Error creating dummy files");
    }
  };

  const runSimulation = async () => {
    try {
      const token = localStorage.getItem("token"); // login ke baad token store hona chahiye

      const res = await fetch(
        `http://127.0.0.1:8000/simulate/run?token=${token}`,
        { method: "POST" }
      );
      const data = await res.json();
      setResult(data);

      // ransom note content fetch
      const noteRes = await fetch(
        `http://127.0.0.1:8000/simulate/ransom-note?token=${token}`
      );
      if (noteRes.ok) {
        const noteData = await noteRes.json();
        setNote(noteData.content);
      }
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Error running simulation");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-6">Simulator</h2>
      <p className="mb-4">Run attack simulations for SOC testing.</p>

      {/* Buttons */}
      <div className="space-x-4">
        <button
          onClick={createFiles}
          className="bg-blue-500 text-white px-4 py-2 rounded shadow"
        >
          Create Dummy Files
        </button>
        <button
          onClick={runSimulation}
          className="bg-red-500 text-white px-4 py-2 rounded shadow"
        >
          Run Simulation
        </button>
      </div>

      {/* Message */}
      {message && (
        <div className="mt-4 p-3 bg-gray-100 rounded shadow text-black">
          {message}
        </div>
      )}

      {/* Simulation Result */}
      {result && (
        <div className="mt-6 space-y-4">
          <div className="p-4 bg-gray-800 text-white rounded-lg shadow">
            <p><strong>Status:</strong> {result.status}</p>
            <p><strong>Renamed Files:</strong> {result.renamed}</p>
            <p><strong>Ransom Note Path:</strong> {result.ransom_note}</p>
          </div>

          {note && (
            <div className="p-4 bg-black text-green-400 rounded-lg shadow">
              <h3 className="text-lg font-bold mb-2">üìù Ransom Note Content</h3>
              <pre className="whitespace-pre-wrap">{note}</pre>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
