import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Simulator() {
  const [result, setResult] = useState(null);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // ✅ Agar token missing hai to redirect to login
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    }
  }, [navigate]);

  // ✅ Create dummy files
  const createDummyFiles = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://127.0.0.1:8000/simulate/create-files", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!res.ok) throw new Error("Failed: " + res.status);

      const data = await res.json();
      setMessage(`✅ Created ${data.created} dummy files`);
    } catch (err) {
      console.error("Error creating files:", err);
      setMessage("❌ Error creating dummy files");
    }
  };

  // ✅ Run simulation
  const runSimulation = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch("http://127.0.0.1:8000/simulate/run", {
        method: "POST",
        headers: {
          "Authorization": `Bearer ${token}`,
        },
      });

      if (!res.ok) throw new Error("Simulation failed: " + res.status);

      const data = await res.json();
      setResult(data);
      setMessage("✅ Simulation completed successfully!");
    } catch (err) {
      console.error("Error running simulation:", err);
      setMessage("❌ Error running simulation");
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Simulator</h2>
      <p className="mb-4">Run attack simulations for SOC testing.</p>

      {/* Buttons */}
      <div className="flex gap-4 mb-4">
        <button
          onClick={createDummyFiles}
          className="bg-blue-500 text-white px-4 py-2 rounded shadow-md hover:bg-blue-600"
        >
          Create Dummy Files
        </button>

        <button
          onClick={runSimulation}
          className="bg-red-500 text-white px-4 py-2 rounded shadow-md hover:bg-red-600"
        >
          Run Simulation
        </button>
      </div>

      {/* Message */}
      {message && <p className="mt-2 text-yellow-300">{message}</p>}

      {/* Simulation result */}
      {result && (
        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="p-6 rounded-xl shadow-lg text-white bg-blue-600">
            <h3 className="text-lg font-semibold">Status</h3>
            <p className="text-2xl font-bold mt-2">{result.status}</p>
          </div>

          <div className="p-6 rounded-xl shadow-lg text-white bg-green-600">
            <h3 className="text-lg font-semibold">Renamed Files</h3>
            <p className="text-2xl font-bold mt-2">{result.renamed}</p>
          </div>

          <div className="p-6 rounded-xl shadow-lg text-white bg-purple-600">
            <h3 className="text-lg font-semibold">Ransom Note</h3>
            <p className="text-sm mt-2 break-all">{result.ransom_note}</p>
          </div>
        </div>
      )}
    </div>
  );
}
