import axios from "axios";

// Apna backend URL yaha set karo
const API = axios.create({
  baseURL: "http://127.0.0.1:8000", // FastAPI backend
});

// -----------------------------
// Incidents
// -----------------------------
export const fetchIncidents = async (limit = 100) => {
  const res = await API.get(`/incidents?limit=${limit}`);
  return res.data;
};

// -----------------------------
// Simulator
// -----------------------------
export const createDummyFiles = async (count = 50) => {
  const res = await API.post(`/simulate/create-files?count=${count}`);
  return res.data;
};

export const runSimulation = async (token) => {
  const res = await API.post(
    "/simulate/run",
    {},
    { headers: { Authorization: `Bearer ${token}` } }
  );
  return res.data;
};

// -----------------------------
// Responder
// -----------------------------
export const quarantineAll = async (token) => {
  const res = await API.post(
    "/response/quarantine",
    {},
    { headers: { Authorization: `Bearer ${token}` } }
  );
  return res.data;
};

// -----------------------------
// Reports
// -----------------------------
export const generateReport = async (incidentId, token) => {
  const res = await API.post(
    `/report/generate?incident_id=${incidentId}`,
    {},
    { headers: { Authorization: `Bearer ${token}` } }
  );
  return res.data;
};
