import { useState } from "react";

export default function Simulator() {
  const [result, setResult] = useState(null);

  const runSimulation = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await fetch(`http://127.0.0.1:8000/simulate/run?token=${token}`, {
        method: "POST",
      });

      const data = await res.json();
      setResult(data);
    } catch (err) {
      console.error("Error running simulation:", err);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Simulator</h2>
      <p className="mb-4">Run attack simulations for SOC testing.</p>

      <button
        onClick={runSimulation}
        className="bg-red-500 text-white px-4 py-2 rounded shadow-md hover:bg-red-600"
      >
        Run Simulation
      </button>

      {result && (
        <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="p-6 rounded-xl shadow-lg text-white bg-blue-600">
            <h3 className="text-lg font-semibold">Status</h3>
            <p className="text-2xl font-bold mt-2">{result.status}</p>
          </div>

          <div className="p-6 rounded-xl shadow-lg text-white bg-green-600">
            <h3 className="text-lg font-semibold">Renamed Files</h3>
            <p className="text-2xl font-bold mt-2">{result.renamed}</p>
          </div>

          <div className="p-6 rounded-xl shadow-lg text-white bg-purple-600">
            <h3 className="text-lg font-semibold">Ransom Note</h3>
            <p className="text-sm mt-2 break-all">{result.ransom_note}</p>
          </div>
        </div>
      )}
    </div>
  );
}
