import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Incidents() {
  const [incidents, setIncidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login"); // üîÅ token missing ‚Üí redirect
        return;
      }

      try {
        const res = await fetch("http://127.0.0.1:8000/incidents?limit=100", {
          headers: {
            "Authorization": `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          if (res.status === 401) {
            localStorage.removeItem("token");
            navigate("/login"); // expired token ‚Üí login
            return;
          }
          throw new Error("Failed to fetch incidents");
        }

        const data = await res.json();
        setIncidents(data);
      } catch (err) {
        setError(err.message || "Error fetching incidents");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate]);

  if (loading) return <p className="text-gray-400">Loading incidents...</p>;
  if (error) return <p className="text-red-400">‚ùå {error}</p>;

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Incidents</h2>
      {incidents.length === 0 ? (
        <p className="text-gray-400">No incidents found.</p>
      ) : (
        <table className="table-auto w-full text-sm border border-gray-700">
          <thead className="bg-gray-800 text-gray-200">
            <tr>
              <th className="px-4 py-2">Type</th>
              <th className="px-4 py-2">Details</th>
              <th className="px-4 py-2">Timestamp</th>
              <th className="px-4 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {incidents.map((inc) => (
              <tr key={inc.id} className="border-b border-gray-700">
                <td className="px-4 py-2">{inc.type}</td>
                <td className="px-4 py-2">{JSON.stringify(inc.details)}</td>
                <td className="px-4 py-2">{inc.timestamp}</td>
                <td className="px-4 py-2">
                  <button className="bg-blue-500 text-white px-2 py-1 rounded">
                    Download Report
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
