import { useState } from "react";
import { generateReport } from "../services/api";

export default function Reports() {
  const [incidentId, setIncidentId] = useState("");
  const [report, setReport] = useState(null);
  const [error, setError] = useState("");

  const handleGenerate = async () => {
    try {
      const token = "dummy_token"; // Demo ke liye
      const data = await generateReport(incidentId, token);
      setReport(data);
      setError("");
    } catch (err) {
      setError("Report generate karne me error: " + err.message);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-gray-100 p-6">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Generate Report</h2>

        <div className="flex gap-2 mb-4">
          <input
            type="text"
            placeholder="Enter Incident ID"
            value={incidentId}
            onChange={(e) => setIncidentId(e.target.value)}
            className="flex-1 border border-gray-300 p-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleGenerate}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
          >
            Generate
          </button>
        </div>

        {error && <p className="text-red-500 text-center mb-4">{error}</p>}

        {report && (
          <div className="mt-4 p-4 bg-gray-50 border border-gray-200 rounded shadow-sm">
            <h3 className="font-semibold mb-2 text-gray-700">Report Result:</h3>
            <pre className="text-sm text-gray-800 overflow-x-auto">{JSON.stringify(report, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
}
