import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Simulator() {
  const [result, setResult] = useState(null);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/login");
  }, [navigate]);

  const createDummyFiles = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }
      const res = await fetch("http://127.0.0.1:8000/simulate/create-files", {
        method: "POST",
        headers: { "Authorization": `Bearer ${token}` },
      });
      if (!res.ok) throw new Error(`Failed: ${res.status}`);
      const data = await res.json();
      setMessage(`✅ Created ${data.created} dummy files`);
    } catch (err) {
      console.error(err);
      setMessage("❌ Error creating dummy files");
    }
  };

  const runSimulation = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/login");
        return;
      }
      const res = await fetch("http://127.0.0.1:8000/simulate/run", {
        method: "POST",
        headers: { "Authorization": `Bearer ${token}` },
      });
      if (!res.ok) throw new Error(`Simulation failed: ${res.status}`);
      const data = await res.json();
      setResult(data);
      setMessage("✅ Simulation completed successfully!");
    } catch (err) {
      console.error(err);
      setMessage("❌ Error running simulation");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-3xl font-bold mb-2 text-center">Simulator</h2>
        <p className="text-gray-600 mb-6 text-center">
          Run attack simulations for SOC testing
        </p>

        {/* Buttons */}
        <div className="flex flex-col md:flex-row gap-4 justify-center mb-6">
          <button
            onClick={createDummyFiles}
            className="flex-1 bg-blue-600 text-white py-3 rounded shadow hover:bg-blue-700 transition"
          >
            Create Dummy Files
          </button>
          <button
            onClick={runSimulation}
            className="flex-1 bg-red-600 text-white py-3 rounded shadow hover:bg-red-700 transition"
          >
            Run Simulation
          </button>
        </div>

        {/* Message */}
        {message && (
          <p
            className={`text-center mb-6 font-medium ${
              message.startsWith("✅") ? "text-green-600" : "text-red-600"
            }`}
          >
            {message}
          </p>
        )}

        {/* Simulation Result */}
        {result && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="p-6 rounded-xl shadow-lg bg-blue-600 text-white">
              <h3 className="text-lg font-semibold">Status</h3>
              <p className="text-2xl font-bold mt-2">{result.status}</p>
            </div>
            <div className="p-6 rounded-xl shadow-lg bg-green-600 text-white">
              <h3 className="text-lg font-semibold">Renamed Files</h3>
              <p className="text-2xl font-bold mt-2">{result.renamed}</p>
            </div>
            <div className="p-6 rounded-xl shadow-lg bg-purple-600 text-white">
              <h3 className="text-lg font-semibold">Ransom Note</h3>
              <p className="text-sm mt-2 break-words">{result.ransom_note}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
