import { useEffect, useState } from "react";
import {
  PieChart,
  Pie,
  Cell,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Legend,
  AreaChart,
  Area,
} from "recharts";
import { fetchIncidents } from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [incidents, setIncidents] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const loadData = async () => {
      try {
        const data = await fetchIncidents();
        setIncidents(data);
      } catch (err) {
        setError(err.message);
        navigate("/login");
      }
    };
    loadData();
  }, [navigate]);

  // === existing logic kept same ===
  const typeCount = incidents.reduce((acc, i) => {
    acc[i.type] = (acc[i.type] || 0) + 1;
    return acc;
  }, {});
  const pieData = Object.entries(typeCount).map(([name, value]) => ({ name, value }));

  const COLORS = ["#6366F1", "#10B981", "#FBBF24", "#EF4444", "#3B82F6"];

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 p-6">
      <h2 className="text-3xl font-extrabold mb-8 text-center tracking-wide">
        ðŸ“Š Dashboard Overview
      </h2>

      {error && <p className="text-red-400 text-center mb-4">{error}</p>}
      {incidents.length === 0 && (
        <p className="text-gray-400 text-center mb-4">No incidents found.</p>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Pie Chart Card */}
        <div className="bg-gray-800/80 backdrop-blur-xl shadow-xl rounded-2xl p-6 h-96 hover:shadow-2xl transition">
          <h3 className="text-lg font-semibold mb-4 text-center">Incident Distribution</h3>
          <ResponsiveContainer>
            <PieChart>
              <Pie data={pieData} dataKey="value" nameKey="name" outerRadius={100} label>
                {pieData.map((_, i) => (
                  <Cell key={i} fill={COLORS[i % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip
                contentStyle={{ backgroundColor: "#1F2937", border: "none", color: "#fff" }}
              />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Bar Chart Card */}
        <div className="bg-gray-800/80 backdrop-blur-xl shadow-xl rounded-2xl p-6 h-96 hover:shadow-2xl transition">
          <h3 className="text-lg font-semibold mb-4 text-center">Incident Frequency</h3>
          <ResponsiveContainer>
            <BarChart data={pieData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="name" stroke="#D1D5DB" />
              <YAxis stroke="#D1D5DB" />
              <Tooltip
                contentStyle={{ backgroundColor: "#1F2937", border: "none", color: "#fff" }}
              />
              <Legend />
              <defs>
                <linearGradient id="colorValue" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#10B981" stopOpacity={0.9} />
                  <stop offset="95%" stopColor="#3B82F6" stopOpacity={0.9} />
                </linearGradient>
              </defs>
              <Bar dataKey="value" fill="url(#colorValue)" radius={[8, 8, 0, 0]} />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* NEW: Area Chart Card */}
        <div className="bg-gray-800/80 backdrop-blur-xl shadow-xl rounded-2xl p-6 h-96 hover:shadow-2xl transition">
          <h3 className="text-lg font-semibold mb-4 text-center">Type Trend (Area)</h3>
          <ResponsiveContainer>
            <AreaChart data={pieData}>
              <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
              <XAxis dataKey="name" stroke="#D1D5DB" />
              <YAxis stroke="#D1D5DB" />
              <Tooltip
                contentStyle={{ backgroundColor: "#1F2937", border: "none", color: "#fff" }}
              />
              <Legend />
              <defs>
                <linearGradient id="colorArea" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#6366F1" stopOpacity={0.9} />
                  <stop offset="95%" stopColor="#6366F1" stopOpacity={0.2} />
                </linearGradient>
              </defs>
              <Area
                type="monotone"
                dataKey="value"
                stroke="#6366F1"
                fill="url(#colorArea)"
                strokeWidth={2.5}
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}
