import { useState } from "react";
import { generateReport } from "../services/api";

export default function Reports() {
  const [incidentId, setIncidentId] = useState("");
  const [report, setReport] = useState(null);
  const [error, setError] = useState("");

  const handleGenerate = async () => {
    try {
      // Demo ke liye token ko "dummy_token" set kiya hai
      const token = "dummy_token"; 
      const data = await generateReport(incidentId, token);
      setReport(data);
      setError("");
    } catch (err) {
      setError("Report generate karne me error: " + err.message);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Generate Report</h2>

      <input
        type="text"
        placeholder="Enter Incident ID"
        value={incidentId}
        onChange={(e) => setIncidentId(e.target.value)}
        className="border p-2 mr-2 rounded"
      />
      <button
        onClick={handleGenerate}
        className="bg-blue-600 text-white px-4 py-2 rounded"
      >
        Generate
      </button>

      {error && <p className="text-red-500 mt-3">{error}</p>}

      {report && (
        <div className="mt-4 p-4 bg-gray-100 rounded">
          <h3 className="font-semibold">Report Result:</h3>
          <pre className="text-sm">{JSON.stringify(report, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
