import { useState } from "react";

export default function Simulator() {
  const [result, setResult] = useState(null);

  const runSimulation = async () => {
    try {
      const token = localStorage.getItem("token"); // Login ke baad yaha save karna hoga
      const res = await fetch(`http://127.0.0.1:8000/simulate/run?token=${token}`, {
        method: "POST"
      });

      const data = await res.json();
      setResult(data);
    } catch (err) {
      console.error("Error running simulation:", err);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Simulator</h2>
      <p className="mb-4">Run attack simulations for SOC testing.</p>

      <button
        onClick={runSimulation}  // ✅ Button click se function call
        className="bg-red-500 text-white px-4 py-2 rounded"
      >
        Run Simulation
      </button>

      {result && (
        <div className="mt-6 p-4 border rounded bg-gray-100">
          <p><strong>Status:</strong> {result.status}</p>
          <p><strong>Renamed Files:</strong> {result.renamed}</p>
          <p><strong>Ransom Note:</strong> {result.ransom_note}</p>
        </div>
      )}
    </div>
  );
}
